        { value: 'unsure', label: '‰∏çÁ¢∫ÂÆö', icon: '‚ùì' }
      ]
    },
    {
      id: 4,
      question: "ÊÇ®ÁõÆÂâçÁöÑË≤°ÂãôÁãÄÊ≥ÅÊòØÔºü",
      options: [
        { value: 'beginner', label: 'ÂâõÈñãÂßã', icon: 'üå±' },
        { value: 'established', label: 'Â∑≤Âª∫Á´ã', icon: 'üè¢' },
        { value: 'advanced', label: 'ÈÄ≤Èöé', icon: 'üéØ' },
        { value: 'complex', label: 'Ë§áÈõúÈúÄÊ±Ç', icon: 'üß©' }
      ]
    },
    {
      id: 5,
      question: "ÊÇ®Â∏åÊúõÂ¶Ç‰ΩïËàáÈ°ßÂïèÊ∫ùÈÄöÔºü",
      options: [
        { value: 'in-person', label: 'Èù¢Â∞çÈù¢ÊúÉË≠∞', icon: 'ü§ù' },
        { value: 'video', label: 'Ë¶ñË®äÈÄöË©±', icon: 'üìπ' },
