        { value: 'digital', label: 'Êï∏‰Ωç/ÊñáÂ≠ó', icon: 'üí¨' }
      ]
    }
  ] : [
    {
      id: 1,
      question: "What is your primary financial goal?",
      options: [
        { value: 'retirement', label: 'Retirement Planning', icon: 'üèñÔ∏è' },
        { value: 'investment', label: 'Investment Growth', icon: 'üìà' },
        { value: 'debt', label: 'Debt Management', icon: 'üí≥' },
        { value: 'tax', label: 'Tax Optimization', icon: 'üìä' }
      ]
    },
    {
      id: 2,
      question: "What is your investment timeline?",
      options: [
        { value: 'short', label: '1-3 years', icon: '‚ö°' },
        { value: 'medium', label: '3-10 years', icon: 'üìÖ' },
        { value: 'long', label: '10+ years', icon: 'üå±' },
        { value: 'flexible', label: 'Flexible', icon: 'üîÑ' }
      ]
    },
    {
      id: 3,
      question: "What is your risk tolerance?",
      options: [
        { value: 'conservative', label: 'Conservative', icon: 'üõ°Ô∏è' },
        { value: 'moderate', label: 'Moderate', icon: '‚öñÔ∏è' },
        { value: 'aggressive', label: 'Aggressive', icon: 'üöÄ' },
        { value: 'unsure', label: 'Not Sure', icon: '‚ùì' }
      ]
    },
    {
      id: 4,
      question: "What is your current financial situation?",
      options: [
        { value: 'beginner', label: 'Just Starting Out', icon: 'üå±' },
        { value: 'established', label: 'Established', icon: 'üè¢' },
        { value: 'advanced', label: 'Advanced', icon: 'üéØ' },
        { value: 'complex', label: 'Complex Needs', icon: 'üß©' }
      ]
    },
    {
      id: 5,
      question: "How do you prefer to communicate with your advisor?",
      options: [
        { value: 'in-person', label: 'In-Person Meetings', icon: 'ü§ù' },
        { value: 'video', label: 'Video Calls', icon: 'üìπ' },
        { value: 'phone', label: 'Phone Calls', icon: 'üìû' },
        { value: 'digital', label: 'Digital/Text', icon: 'üí¨' }
      ]
    }
  ];

  const matchedAgents = [
    {
      id: 1,
      name: 'Sarah Johnson',
      specialty: language === 'zh-TW' ? 'ÈÄÄ‰ºëË¶èÂäÉ' : 'Retirement Planning',
      experience: '15+ years',
      rating: 4.9,
      image: 'https://images.unsplash.com/photo-1494790108755-2616b612b786?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D&auto=format&fit=crop&w=687&q=80',
      matchScore: 95,
      description: language === 'zh-TW' 
