      matchScore: 92,
      description: language === 'zh-TW'
        ? 'Â∞àÁ≤æÊñºÈï∑ÊúüË≤°ÂØåÂª∫Ë®≠ÁöÑÊàêÈï∑Â∞éÂêëÊäïË≥áÁ≠ñÁï•„ÄÇ'
        : 'Specializes in growth-oriented investment strategies for long-term wealth building.'
    },
    {
      id: 3,
      name: 'Emily Rodriguez',
      specialty: language === 'zh-TW' ? 'Á®ÖÂãôË¶èÂäÉ' : 'Tax Planning',
      experience: '10+ years',
      rating: 4.7,
      image: 'https://images.unsplash.com/photo-1438761681033-6461ffad8d80?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D&auto=format&fit=crop&w=687&q=80',
      matchScore: 88,
      description: language === 'zh-TW'
        ? 'Á®ÖÂãôÂÑ™ÂåñÂ∞àÂÆ∂ÔºåÂπ´Âä©ÂÆ¢Êà∂ÊúÄÂ§ßÂåñË≤°ÂãôÊïàÁéá„ÄÇ'
        : 'Tax optimization expert helping clients maximize their financial efficiency.'
    }
  ];

  const handleAnswer = (answer) => {
    setAnswers({ ...answers, [currentQuestion]: answer });
  };

  const handleNext = () => {
    if (currentQuestion < questions.length - 1) {
      setCurrentQuestion(currentQuestion + 1);
    } else {
      setShowResults(true);
    }
  };

  const handlePrevious = () => {
    if (currentQuestion > 0) {
      setCurrentQuestion(currentQuestion - 1);
    }
  };

  const handleRestart = () => {
    setCurrentQuestion(0);
    setAnswers({});
    setShowResults(false);
  };

  if (showResults) {
    return (
      <div className="min-h-screen bg-gray-50">
        {/* Hero Section */}
        <section className="py-20 bg-gradient-to-r from-green-600 to-blue-600 text-white">
          <div className="max-w-7xl mx-auto px-4 text-center">
            <h1 className="text-5xl font-bold mb-6">
              {language === 'zh-TW' ? 'ÊÇ®ÁöÑÂÆåÁæéÈÖçÂ∞ç' : 'Your Perfect Matches'}
